package testprogram;

public class Switch {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//13)Switch case-we can use the switch case method for multiple choice&expression
   int rank=3;
   switch(rank)
   {
	   case 1:
		   System.out.println("average");break;
	   case 2:
		   System.out.println("medium");break;
	   case 3:
		   System.out.println("topper");break;
	   default:
		   System.out.println("Almost pass");
		   
   }
	}

}
